mapper.select:


SELECT memberId, memberName, memberPw
FROM members 
WHERE memberId = #{memberId} AND memberPw = #{memberPw}



SELECT memberId, memberName, memberPw
FROM members 
WHERE memberId = #{memberId}

mapper.update:

UPDATE members
SET memberName=#{memberName}, memberPw=#{memberPw}
WHERE memberId = #{memberId}


UPDATE members
SET memberName='', memberPw=''
WHERE memberId = #{memberId}
